import React, { useReducer } from "react";

import "./App.css";

import Board from './components/Board';

import { boardReducer } from "./reducers/boardReducer";
import { GameInitialState } from "./constants/gameConstants";
import { BoardActions } from "./actions/BoardActions";

export const stateContext = React.createContext();
export const dispatchContext = React.createContext();

function App() {
  const [state, dispatch] = useReducer(boardReducer, GameInitialState);

  const onResetClick = () => {
    dispatch({ type: BoardActions.INITIATE_DUMMY_BOARD, payload: { level: state.level } });
  }

  return (
    <stateContext.Provider value={state}>
      <dispatchContext.Provider value={dispatch}>
        <div>

        </div>
        <div className='bg-primary min-h-screen pt-24 text-center'>
          { state.gameClear && <h1 className='mx-auto'>YOU WON</h1>}
          <div className='uppercase font-medium text-6xl text-secondary'><span className='text-quinary'>mine</span>sweeper</div>
          <div>
          </div>
        <div className='w-full pt-48'>
          {/* <div className="game_minesLeft">{state.minesLeft}</div> */}
          <Board />
        </div>
        </div>
      </dispatchContext.Provider>
    </stateContext.Provider>
  )
}

export default App;
