import React, { useReducer } from "react";

import "./App.css";

import Board from "./components/Board";
import CustomSelect from './components/CustomSelect';

import { boardReducer } from "./reducers/boardReducer";
import { GameLevel, GameInitialState } from "./constants/gameConstants";
import { BoardActions } from "./actions/BoardActions";

import { RiRestartFill } from "react-icons/ri";

export const stateContext = React.createContext();
export const dispatchContext = React.createContext();

const levels = [GameLevel.EASY.name, GameLevel.INTERMEDIATE.name, GameLevel.EXPERT.name]

function App() {
  const [state, dispatch] = useReducer(boardReducer, GameInitialState);

  const onResetClick = () => {
    dispatch({
      type: BoardActions.INITIATE_DUMMY_BOARD,
      payload: { level: state.level },
    });
  };

  function onLevelSelect(event) {
    dispatch({
      type: BoardActions.CHANGE_LEVEL,
      payload: { level: GameLevel[event.target.value] },
    });
  }

  return (
    <stateContext.Provider value={state}>
      <dispatchContext.Provider value={dispatch}>
        <div></div>
        <div className="bg-primary min-h-screen pt-40 text-center">
          {state.gameClear && <h1 className="mx-auto">YOU WON</h1>}
          <div className="uppercase font-medium text-6xl text-quinary">
            minesweeper
          </div>
          <div></div>
          <div className="w-full pt-24 text-quinary">
            <div className="flex mb-12 mx-auto w-96 justify-around items-center">
              <div className="text-lg">Mines left : {state.minesLeft}</div>
              <div className='flex items-center gap-x-2'>
                <button
                  className="transition-all duration-1000 bg-transparent border-2 border-secondary text-secondary hover:text-quaternary hover:border-quaternary p-1 text-3xl rounded-lg"
                  onClick={onResetClick}
                >
                  <RiRestartFill />
                </button>
                <CustomSelect defText='INTERMEDIATE' />
                {/* <select
                  className="text-secondary"
                  value={state.level.name}
                  onChange={(e) => onLevelSelect(e)}
                >
                  <option value={GameLevel.EASY.name}>Easy</option>
                  <option value={GameLevel.INTERMEDIATE.name}>
                    Intermediate
                  </option>
                  <option value={GameLevel.EXPERT.name}>Expert</option>
                </select> */}
              </div>
            </div>

            <Board />
          </div>
        </div>
      </dispatchContext.Provider>
    </stateContext.Provider>
  );
}

export default App;
