import React, { useEffect, useContext } from "react";

import Cell from "./Cell";

import { stateContext, dispatchContext } from "../App";
import { BoardActions } from "../actions/BoardActions";

import { GameLevel } from "../constants/gameConstants";

const Board = () => {
  const state = useContext(stateContext);
  const dispatch = useContext(dispatchContext);

  useEffect(() => {
    dispatch({
      type: BoardActions.INITIATE_DUMMY_BOARD,
      payload: { level: GameLevel.INTERMEDIATE },
    });
  }, [dispatch]);

  return (
    <div>
     {state.gameOver && <h1>GAME OVER</h1>} 
    <div
      className="grid w-min"
      style={{
        gridTemplateRows: `repeat(${state.level.rows}, 32px)`,
        gridTemplateColumns: `repeat(${state.level.columns}, 32px)`,
      }}
    >
      {state.board && state.board.map((row, i) => {
        return row.map((cell, j) => {
          return (
            <Cell
              cellInfo={{
                ...cell,
                row: i,
                column: j,
              }}
              key={[i, j]}
            />
          );
        });
      })}
    </div>
    </div>
  );
};

export default Board;
