import React, { useContext, useEffect } from "react";

import { FaBomb } from "react-icons/fa";
import { BsFlagFill } from "react-icons/bs";
import {AiFillFire} from 'react-icons/ai'

import { dispatchContext, stateContext } from "../App";
import { BoardActions } from "../actions/BoardActions";

const Cell = (props) => {
  const dispatch = useContext(dispatchContext);
  const { gameClear ,gameOver, level, clickNumber } = useContext(stateContext);

  let {
    hasMine,
    isUncovered,
    numMinesAround,
    row,
    column,
    flagged,
  } = props.cellInfo;

  useEffect(()=> {
    if(isUncovered && hasMine) dispatch({ type: BoardActions.GAME_OVER });
  }, [isUncovered, dispatch, hasMine])

  const onCellClick = () => {
    if (flagged || gameOver || gameClear) return;
    if (clickNumber === 0) {
      dispatch({
        type: BoardActions.INITIATE_BOARD,
        payload: { level, row, column },
      });
    }
    dispatch({ type: BoardActions.UNCOVER_CELL, payload: { row, column }});
  };

  function onCellDoubleClick() {
    if (gameOver) return;
    dispatch({ type: BoardActions.DOUBLE_CLICK_CELL, payload: { row, column }})
  }

  const onCellRightClick = (e) => {
    e.preventDefault();
    e.stopPropagation();
    if(gameOver || isUncovered || gameClear) return;
    dispatch({type:BoardActions.RIGHT_CLICK_CELL, payload: { row, column }});
    return false;
  }



  return (
    <>
      <div
        className={`cell ${isUncovered ? 'bg-blue-300 uncovered' : '' } inline-flex justify-center items-center`}
        onClick={onCellClick}
        onDoubleClick={onCellDoubleClick}
        onContextMenu={(e) => onCellRightClick(e)}
      >
        {/* { && <FaBomb />} */}
        {/* { hasMine && gameOver && !isUncovered ? <FaBomb /> : null}
        { (hasMine && isUncovered) ? <AiFillFire /> : null}
        {(flagged && !gameOver) ? <BsFlagFill /> : ''}
        {(numMinesAround && !hasMine && isUncovered) ? numMinesAround : ''} */}
      </div>
      <style jsx>{`
        .cell {
          border-top: 2px solid black;
          border-left: 2px solid black;
          border-right: 2px solid yellow;
          border-bottom: 2px solid yellow;
        }
        .cell .uncovered {
          border: 0.5px solid gray;
        }
      `}</style>
    </>
  );
};

export default Cell;
