export const GameLevel = Object.freeze({
    EASY: {
        name: 'EASY',
        rows: 10,
        columns: 10,
        num_mine: 12
    },
    INTERMEDIATE: {
        name: 'INTERMEDIATE',
        rows: 20,
        columns: 20,
        num_mine: 50
    },

    EXPERT: {
        name: 'EXPERT',
        rows: 32,
        columns: 32,
        num_mine: 150
    }
})

export const GameInitialState = {
    board: [],
    level: GameLevel.EASY,
    gameOver: false,
    minesLeft: GameLevel.EASY.num_mine,
    gameClear: false,
    clickNumber: 0
}
